import 'dart:convert';
import 'package:flutter/material.dart';
import 'home_page.dart'; // La pantalla de la lista de dispositivos

class WelcomePage extends StatelessWidget {
  const WelcomePage({super.key});

  // --- PEGA AQUÍ TU LOGO EN BASE64 ---
  // Reemplaza esta cadena con la tuya.
  final String logoBase64 = '';

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        // El fondo con gradiente se mantiene igual
        decoration: BoxDecoration(
          gradient: LinearGradient(
            begin: Alignment.topLeft,
            end: Alignment.bottomRight,
            colors: [Colors.green.shade400, Colors.green.shade700], // Usando los colores de tu captura
          ),
        ),
        // Usamos un Column para organizar el espacio verticalmente
        child: Column(
          children: <Widget>[
            // --- SECCIÓN 1: ESPACIO SUPERIOR CON EL LOGO ---
            // Expanded hace que esta sección ocupe una porción del espacio disponible.
            Expanded(
              flex: 5, // Le damos 5 'partes' del espacio. Aumenta este número para bajar el logo.
              child: Center( // Centramos el logo dentro de esta sección
                child: Image.memory(
                  base64Decode(logoBase64),
                  width: 200,
                ),
              ),
            ),

            // --- SECCIÓN 2: CONTENIDO CENTRAL (TEXTO Y BOTÓN) ---
            // Esta sección no es 'Expanded', por lo que ocupará solo el espacio que necesite.
            Column(
              mainAxisSize: MainAxisSize.min,
              children: [
                const Text(
                  'Control Panel CP',
                  style: TextStyle(
                    fontSize: 28,
                    fontWeight: FontWeight.bold,
                    color: Colors.white,
                    shadows: [Shadow(blurRadius: 2.0, color: Colors.black26, offset: Offset(1.0, 1.0))],
                  ),
                ),
                const SizedBox(height: 50),
                ElevatedButton.icon(
                  icon: const Icon(Icons.bluetooth_searching),
                  label: const Text('Buscar Dispositivo'),
                  style: ElevatedButton.styleFrom(
                    padding: const EdgeInsets.symmetric(horizontal: 30, vertical: 15),
                    textStyle: const TextStyle(fontSize: 18),
                  ),
                  onPressed: () {
                    Navigator.of(context).pushReplacement(
                      MaterialPageRoute(builder: (context) => const HomePage()),
                    );
                  },
                ),
              ],
            ),

            // --- SECCIÓN 3: ESPACIO INFERIOR VACÍO ---
            // Este Expanded empuja las otras dos secciones hacia arriba.
            Expanded(
              flex: 4, // Le damos 4 'partes'. Aumenta este número para subir todo el contenido.
              child: Container(), // Un contenedor vacío que solo ocupa espacio
            ),
          ],
        ),
      ),
    );
  }
}